pragma solidity 0.4.24;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "./ManagerInterface.sol";
import "./Escrow.sol";

/// @title Stream smart contract 
/// @dev Represents a video transcode job - is created by a client.
/// The video is split in chunks; miners submit proofs for each chunk for each bitrate.
/// Validators approve miner proofs.
/// Because the solidity 0.4.24 compiler does not support ABI encoding for 
/// structs(except with the experimental flag which casts all the values to strings)
/// we sometimes return the 'destructured' objects.
/// The contract holds a list of input chunk ids (_inChunkIds) and of requested bitrates (_bitrates).
/// Each requested bitrate has an Outstream and each Outstream stores a dictionary of 
/// proof queues mapped for each input chunk. The proof queues store the proofs & ouput chunks
/// submited by miners.

contract Stream is Escrow {
  using SafeMath for uint256;

  struct Proof {
    address miner;
    uint256 outputChunkId;
    uint256 proof;
  }

  /// @notice Simple queue-like data structure used for proof submission and validation.
  /// @dev Head is used to determine FIFO order for miner proof submission & validation.
  /// New proofs are pushed at the back of the array. Head increments when a proof discarded
  /// and it points at oldest proof (first to be validated).
  /// A proof is valid if ChunkProofQueue.validator != address(0).
  /// The proof is found at ChunkProofQueue.proofs[ChunkProofQueue.head]
  struct ChunkProofQueue {
    Proof[] proofs;
    uint256 head;
    address validator;
  }

  /// @title OutStream
  /// @dev The struct holds a map from chunkId to proof queues.
  /// There is one OutStream per bitrate requested by client.
  struct OutStream {
    bool required;
    uint256 validatedChunks;
    mapping (uint256 => ChunkProofQueue) proofQueues;
  }

  address public manager;
  uint256 public id;
  string public RTMP;
  bool public ended;

  mapping (uint256 => bool) public isChunk;
  uint256[] private _inChunkIds;

  mapping (uint256 => OutStream) public outStreams;
  uint256[] private _bitrates;

  /// @notice 
  /// @dev 
  constructor(uint256 _id, address client, uint256[] chunks, uint256[] bitrates, string rtmp) Escrow(client) public {
    id = _id;
    manager = msg.sender;
    RTMP = rtmp;

    _inChunkIds = chunks;
    for(uint i = 0; i < chunks.length; i++) {
      isChunk[chunks[i]] = true;
    }

    _bitrates = bitrates;
    for(i = 0; i < _bitrates.length; i++) {
      OutStream memory outStream;
      outStream.required = true;

      outStreams[_bitrates[i]] = outStream;
    }
  }

  /// @notice Manager can add more input chunkIds as they become available
  /// @dev Reverts if chunk already exists
  function addInputChunkId(uint256 chunkId) public onlyManager {
    require(!isChunk[chunkId] && !ended);

    isChunk[chunkId] = true;
    _inChunkIds.push(chunkId);
  }

  /// @notice Signals that the input stream has ended and no more input chunks are available
  /// @dev Called by manager contract
  function endStream() public onlyManager {
    require(!ended);

    ended = true;
  }

  /// @notice Miners use the method to submit proofs & output chunkIds
  /// @dev Will not allow new proof submission if already validated, i.e. proofQueue.validator != address(0)
  function submitProof(uint256 bitrate, uint256 chunkId, uint256 proof, uint256 outChunkId) public {
    ChunkProofQueue storage proofQueue = outStreams[bitrate].proofQueues[chunkId];
    require(isChunk[chunkId] && proofQueue.validator == address(0));
    require(outStreams[bitrate].required);

    proofQueue.proofs.push(Proof(msg.sender, outChunkId, proof));

    emit ChunkProofSubmited(chunkId, bitrate, proofQueue.proofs.length - 1);
  }

  /// @notice Validators use the method to validate the first proof available in the queue.
  /// @dev Requires that we have proofs.
  /// At the moment we issue a reward of 0.01 VID until we have a wattage/reward calculation.
  /// alpha: we added a call to withdraw to actually transfer funds to miner when validating
  /// (push payment).
  function validateProof(uint256 bitrate, uint256 chunkId) public onlyValidator {
    OutStream storage outStream = outStreams[bitrate];
    require(outStream.required);

    ChunkProofQueue storage proofQueue = outStream.proofQueues[chunkId];
    require(isChunk[chunkId] && proofQueue.validator == address(0));
    require(proofQueue.head < proofQueue.proofs.length);

    address miner = proofQueue.proofs[proofQueue.head].miner;

    uint256 amount = 1e16;
    bool funded = fundAccount(miner, amount);
    if(!funded) return;

    proofQueue.validator = msg.sender;
    outStream.validatedChunks++;

    emit ChunkProofValidated(bitrate, chunkId);
  }

  /// @notice Validators can scrap the first proof available in the queue if they consider so.
  /// @dev This will increment the proofs queue head to point to the next proof.
  /// This we would decrease miner reputation.
  function scrapProof(uint256 bitrate, uint256 chunkId) public onlyValidator {
    ChunkProofQueue storage proofQueue = outStreams[bitrate].proofQueues[chunkId];
    require(isChunk[chunkId] && proofQueue.validator == address(0));
    require(proofQueue.head.add(1) <= proofQueue.proofs.length);

    uint256 idx = proofQueue.head;
    proofQueue.head = proofQueue.head.add(1); 

    emit ChunkProofScrapped(bitrate, chunkId, idx);
  }

  /// @notice Returns true if the chunk has been validated
  /// @dev Reverts if bitrate ore chunk id are wrong/not required.
  function hasValidProof(uint256 bitrate, uint256 chunkId) public view returns (bool) {
    OutStream storage outStream = outStreams[bitrate];
    require(isChunk[chunkId] && outStream.required);

    return outStreams[bitrate].proofQueues[chunkId].validator != address(0);
  }

  /// @notice Returns the proof of a validated chunk.
  /// @dev Requires that the proof has been validated.
  function getValidProof(uint256 bitrate, uint256 chunkId) public view 
                                        returns (address miner,
                                                address validator,
                                                uint256 outputChunkId,
                                                uint256 proof) {
    require(hasValidProof(bitrate, chunkId));
    
    ChunkProofQueue storage proofQueue = outStreams[bitrate].proofQueues[chunkId];

    uint256 idx = proofQueue.head;
    return (proofQueue.proofs[idx].miner, proofQueue.validator, proofQueue.proofs[idx].outputChunkId, proofQueue.proofs[idx].proof);
  }

  /// @notice Returns the first proof canditate for validation/scraping.
  /// @dev Validators should use this to query the current proof canditate before validating it.
  /// Returns object with '0' values  if no candidate proof, i.e.
  /// all submited proof have been scrapped
  /// Reverts if if no proof yet submited or idx out of bounds
  function getCandidateProof(uint256 bitrate, uint256 chunkId) public view 
                                        returns (address miner,
                                                uint256 outputChunkId,
                                                uint256 proof) {
    ChunkProofQueue storage proofQueue = outStreams[bitrate].proofQueues[chunkId];
    uint256 count = proofQueue.proofs.length;
    require(count > 0);

    uint256 idx = proofQueue.head;

    if(idx < count ) {
      return getProof(bitrate, chunkId, idx);
    }

    return (address(0), 0, 0);
  }

  /// @notice Query proof count for given chunk&bitrate.
  /// @dev Reverts if bitrate or chunk id do not exist.
  function getProofCount(uint256 bitrate, uint256 chunkId) public view returns (uint256) {
    ChunkProofQueue storage proofQueue = outStreams[bitrate].proofQueues[chunkId];
    require(outStreams[bitrate].required && isChunk[chunkId]);

    return proofQueue.proofs.length;
  }

  /// @notice Returns any proof that has been submited for the given chunk & bitrate from the
  /// proof queue. 
  /// @dev Can be used to inspect the proof queue for a given chunk & bitrate.
  /// Reverts if bitrate or chunk id do not exist or if idx goes out of range.
  function getProof(uint256 bitrate, uint256 chunkId, uint256 idx) public view 
                                        returns (address miner,
                                                uint256 outputChunkId,
                                                uint256 proof) {
    ChunkProofQueue storage proofQueue = outStreams[bitrate].proofQueues[chunkId];

    require(outStreams[bitrate].required && isChunk[chunkId]);
    require(idx < proofQueue.proofs.length);

    return (proofQueue.proofs[idx].miner, proofQueue.proofs[idx].outputChunkId, proofQueue.proofs[idx].proof );
  }

  /// @notice Returns true if the stream has valid proofs for every chunk for every bitrate
  function isTranscodingDone() public view returns (bool) {
    for(uint i = 0; i < _bitrates.length; i++) {
      if(!isBitrateTranscoded(_bitrates[i]))
        return false;
    }
    return true;
  }
  
  /// @notice Returns true if the bitrate has valid proofs for every chunk.
  /// @dev Reverts if bitrate is wrong/not required.
  function isBitrateTranscoded(uint256 bitrate) public view returns (bool) {
    OutStream storage outStream = outStreams[bitrate];
    require(outStream.required);

    return outStream.validatedChunks == _inChunkIds.length;
  }

  /// @notice Returns the bitrates array.
  function getBitrates() public view returns (uint256[]) {
    return _bitrates;
  }

  /// @notice Returns the input chunk ids array.
  function getInChunks() public view returns (uint256[]) {
    return _inChunkIds;
  }

  /// @notice Returns the number of chunks the input stream has.
  function getInChunkCount() public view returns (uint256) {
    return _inChunkIds.length;
  }

  /// @notice Returns all output chunk ids for the given bitrate only.
  function getOutChunks(uint256 bitrate) public view returns (uint256[]) {
    require(isBitrateTranscoded(bitrate));
    OutStream storage outStream = outStreams[bitrate];

    uint256[] memory outputChunks = new uint256[](_inChunkIds.length);

    for(uint i = 0; i < _inChunkIds.length; i++) {
      uint256 chunkId = _inChunkIds[i];
      ChunkProofQueue storage proofQueue = outStream.proofQueues[chunkId];
      uint256 head = proofQueue.head;
      uint256 outChunkid = proofQueue.proofs[head].outputChunkId;

      outputChunks[i] = outChunkid;
    }

    return outputChunks;
  }

  /// @notice Returns true if the manager allows the refund (special case) or if the stream
  /// has ended and all the chunks have been transcoder
  /// @dev Inherited from Escrow.
  function refundAllowed() public view returns (bool) {
    bool managerPermission = managerInterface(manager).refundAllowed(id);
    bool streamDone = isTranscodingDone() && ended;

    return managerPermission || streamDone;
  }

  /// @notice Modifier for methods only callable by validators
  /// @dev The stream manager holds & manages the validator list.
  modifier onlyValidator() {
    require(managerInterface(manager).isValidator(msg.sender));
    _;
  }

  /// @notice Modifier for methods only callable by the manager contract
  modifier onlyManager() {
    require(msg.sender == manager);
    _;
  }

  /// @dev Events
  event ChunkProofSubmited(uint256 indexed chunkId, uint256 indexed bitrate, uint256 indexed idx);
  event ChunkProofValidated(uint256 indexed bitrate, uint256 indexed chunkId);
  event ChunkProofScrapped(uint256 indexed bitrate, uint256 indexed chunkId, uint256 indexed idx);
}

